import React from 'react';

export function ButtonWrapper({ children, ...rest }){
	return (
		<button
			type="button"
			onMouseOut={() => {
				alert('You left out of button');
			}}
			{...rest}
		>
			{children}
		</button>
	)
}

import React from 'react';
import { createRoot } from 'react-dom/client';

import { ButtonWrapper } from './components/ButtonWrapper';

const root = createRoot(document.getElementById('root'));
root.render(
	<React.StrictMode>
		<h1>Hello World!!</h1>
		<ButtonWrapper>Move mouse out of me and get alert</ButtonWrapper>
	</React.StrictMode>
);

